import { useRef } from "react";
import classes from "./YoutubeForm.module.css";

const YoutubeForm = (props) => {
  const link = useRef();
  const word = useRef();
  const [youtubeLink, setYoutubeLink] = useState("");
  const [queryWord, setQueryWord] = useState("");

  const storeYoutubeLink = (link) => {
    setYoutubeLink(link);
    console.log(youtubeLink);
  };

  const storeQueryWord = (word) => {
    setQueryWord(word);
    console.log(queryWord);
  };

  const submitHandler = (e) => {
    e.preventDefault();
    console.log(link, word);
    storeYoutubeLink(link.current.value);
    props.storeWord(word.current.value);
  };

  return (
    <form className={classes.form} onSubmit={submitHandler}>
      <input placeholder="Youtube Link" ref={link} />
      <input placeholder="Word Query" ref={word} />
      <button type="submit">Find</button>
    </form>
  );
};

export default YoutubeForm;
